function y = boltzmann(units)% y = BOLTZMANN(units) returns Boltzmann constant% Copyright 1999-2008 Michael Frenklach% $Revision: 1.1 $% Last modified: May 18, 2008switch lower(units)   case {'erg/mol_k' 'cgs'}      y = 1.38066e-16;   case {'j/mol_k' 'kj/kmol_k' 'si'}		y = 1.38066e-23;   case {'cm-1' 'wavenumbers'}		y = 0.695162;	otherwise		error(['unknown unit: ' units])end