function f = eval(foObj,T,Pr,data)% f = eval(FalloffObj,T,Pr,data)% evaluates falloff f% Copyright 1999-2010 Michael Frenklach% $Revision: 1.1 $% Last modified: May 7, 2010switch foObj.Type   case 'troe'      troe   case 'sri'      sri   otherwise      error(['type ' foObj.Type ' is not implements'])end   function troe   % Troe's broadenning factor   % data(1:4).param = {alpha T*** T* T**}   % data(1:4).value = [parameter values]      a = [data.value];      F_cent = (1-a(1)) .* exp(-T/a(2)) + ...                  a(1)  .* exp(-T/a(3));             if length(a) > 3         F_cent = F_cent + exp(-a(4)./T);      end      logF_cent = log10(F_cent);      c = -0.4 - 0.67 * logF_cent;      n = 0.75 - 1.27 * logF_cent;      logPrc = log10(Pr) + c;      logF = logF_cent ./ ( 1 + ...             (logPrc./(n - 0.14*logPrc)).^2 );      f = 10.^logF;   end   function sri   % SRI broadenning factor   % data(1:5).param = [a b c d e]   % data(1:5).value      a = [data.value];      x = 1./(1 + log10(Pr).^2);      f = (a(1) * exp(-a(2)./T) + exp(-T./a(3))).^x;      if length(a) > 3         f = f .* a(4) .* T.^a(5);      end   end   end