function [y,u] = eval(k,T,kUnits)% [y,units] = eval(MassActionObj,T,kUnits)% evaluates rate coefficient at given T%% y = eval(k,T,kUnits)  e.g., kUnits = {'cm3/molecule','s'}% y = eval(k,T)         in cm3/mol/s% Copyright 1999-2010 Michael Frenklach% $Revision: 1.0 $% Last modified: May 7, 2010y = eval@ReactionLab.ReactionData.ReactionRate.Arrhenius(k,T);if nargin > 2  && ~isempty(kUnits)   [y,u] = ReactionLab.Units.conv_rate(y,{'cm3/mol','s'},kUnits,k.Order);else   u = ReactionLab.Units.baseRateUnits(k.Order);end