function [y,u] = sum(k,varargin)%[y,units] = sum(MassActionObj,T,kUnits)% sum rate coefficients of array k at given T%%y = sum(k,T,kUnits)  e.g., kUnits = {'cm3/molecule','s'}%y = sum(k,T)         in cm3/mol_s% Copyright 1999-2010 Michael Frenklach% $Revision: 1.0 $% Last modified: May 7, 2010T = varargin{1};units = cell(1,length(k));for i1 = 1:length(k)   if iscell(k)      k1 = k{i1};   else      k1 = k(i1);   end   [yy{i1},units{i1}] = k1.eval(varargin{:});enduu = unique(units);if length(uu) == 1   u = uu{1};else   error(['units are not the same ' char(uu)])end[n,m] = size(T);if n == 1 && m == 1   y = sum([yy{:}]);elseif n == 1   % T is a row vector   y = sum(vertcat(yy{:}),1);elseif m == 1   % T is a column vector   y = sum(horzcat(yy{:}),2);else   error('no temperature value is speciefied')end