function w = molweight(s,rs)% w = molweight(SpeciesObj,rxnsetObj)% Copyright 1999-2010 Michael Frenklach% $Revision: 1.0 $% Last modified: April 29, 2010w = 0;speElems =  s.Elements;if nargin == 2   rsElems = rs.Elements;   for i1 = 1:length(speElems)      el = speElems(i1);      elObj = rsElems.find('Symbol',el.symbol);      w = w + el.number * elObj.Mass;   endelse  %  get from the PrIMe Warehouse   speElems = s.Elements;   for i1 = 1:length(speElems)      el = speElems(i1);      elObj = ReactionLab.SpeciesData.Element(el.symbol);      w = w + el.number * elObj.Mass;   endend