function [y,u,f] = conv_conc(x,units,newUnits)%[newValue,newUnits,conversionFactor] = conv_conc(value,units,newUnits)% convert concentration x from 'units' to 'newUnits'%  newValue = givenValue * f% eg, [y,u,f] = conv_conc(1e-6,'mol/cm3','molecule/cm3')% Copyright 1999-2010 Michael Frenklach% $Revision: 1.1 $% Last modified: May 6, 2010if nargin < 3   error('incorrect number of arguments')end f1    = ReactionLab.Units.conv_factor(units);[f2,u] = ReactionLab.Units.conv_factor(newUnits);f = f2/f1;y = x * f;